mapper.select:
select id, title, content, userid, regtime, views, gubun from board where gubun = #{gubun}
and title like concat('%', #{word}, '%')
order by id desc

select id, title, content, userid, regtime, views, gubun
from board
where id = #{id}

mapper.update:
update board
set title = #{title}, content = #{content}, views = #{views}, gubun = #{gubun} 
where id = #{id}

mapper.select:
select name
from board_name
where gubun = #{gubun}

select id, title, content, userid, regtime, views, gubun
from board
order by views desc limit 5;

mapper.update:
update board_name
set name = #{name}
where gubun = #{gubun}
