mapper.select:
SELECT dongcode, userid, tag, concat(age_range) age_range, family_type, concat(infra) infra, concat(trans) trans, concat(safety) safety, concat(health) health, concat(school) school, concat(environment) environment
FROM dong_review
JOIN members USING(userid);

SELECT dongcode, CONCAT((R1.infra + R1.trans + R1.safety + R1.health + R1.school + R1.environment)/6) as total, CONCAT(R1.infra) infra, CONCAT(R1.trans) trans, CONCAT(R1.safety) safety, CONCAT(R1.health) health, CONCAT(R1.school) school, CONCAT(R1.environment) environment FROM ( SELECT dongcode, AVG(infra) infra, AVG(trans) trans, AVG(safety) safety, AVG(health) health, AVG(school) school, AVG(environment) environment FROM dong_review R JOIN members M USING(userid) WHERE true
AND M.age_range=#{age_range}
AND M.family_type=#{family_type}
GROUP BY dongcode ) R1 JOIN ( SELECT dongcode, AVG(infra) infra, AVG(trans) trans, AVG(safety) safety, AVG(health) health, AVG(school) school, AVG(environment) environment FROM dong_review R JOIN members M USING(userid) WHERE true
AND M.age_range=#{age_range}
AND M.family_type=#{family_type}
GROUP BY dongcode ) R2 USING(dongcode)
