mapper.select:
select d.hno, i.dong, i.aptname, i.code, d.dealAmount, d.dealYear,d.dealMonth, d.dealDay, d.area, i.lat, i.lng, i.jibun from housedeal d left join houseinfo i on d.hno = i.no
where i.dong = #{value}
group by i.jibun;

select gugun from baseaddress
where city = #{value}
group by gugun

select dong from baseaddress
where gugun = #{value}
group by dong

select * 
from corona c 
where c.gungu  in (
	select  distinct gugun
	from baseaddress
	where dongcode = #{value}
);
