mapper.select:
SELECT left(gugun_code,5) gugunCode, gugun_name as gugunName FROM guguncode
WHERE left(gugun_code,2) = #{sido} 
ORDER BY gugun_code

SELECT distinct dong, code FROM houseinfo
where code = #{gugun}
ORDER BY dong

SELECT no,dong,AptName,code,jibun FROM houseinfo WHERE dong
= #{dong}
ORDER BY AptName
